package day_03;

public class Assignment_02_leapYear {
//print the leap years between 2000 and 2100 - Hint : for loop and multiple if-else
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
              int y;
		for (y=2000;y<2101;y++) {
			if (y%4 ==0)
				System.out.println(y + " is a leap year");	
			
		}
		
	}

}
